import * as React from "react";
import {
  GoogleViz,
  GoogleChartWrapper,
  GoogleDataTable,
  ReactGoogleChartProps,
  GoogleChartDashboard,
} from "../types";
import { DEFAULT_CHART_COLORS } from "../constants";

import { loadDataTableFromSpreadSheet } from "../load-data-table-from-spreadsheet";
import { ContextConsumer } from "../Context";

const GRAY_COLOR = "#CCCCCC";
export type ChartDrawArgs = {
  data: ReactGoogleChartProps["data"];
};

export type GoogleChartDataTableProps = {
  googleChartWrapper: GoogleChartWrapper;
  google: GoogleViz;
  googleChartDashboard: GoogleChartDashboard | null;
};
interface State {
  hiddenColumns: string[];
}
export class GoogleChartDataTableInner extends React.Component<
  ReactGoogleChartProps & GoogleChartDataTableProps,
  State
> {
  state = {
    hiddenColumns: [],
  } as State;

  private listenToLegendToggle = () => {
    const { google, googleChartWrapper } = this.props;
    google.visualization.events.addListener(
      googleChartWrapper,
      "select",
      () => {
        const chart = googleChartWrapper.getChart();
        const selection = chart.getSelection();
        const dataTable = googleChartWrapper.getDataTable();
        if (
          selection.length === 0 ||
          // We want to listen to when a whole row is selected. This is the case only when row === null
          selection[0].row ||
          !dataTable
        ) {
          return;
        }
        const columnIndex = selection[0].column;
        const columnID = this.getColumnID(dataTable, columnIndex);
        if (this.state.hiddenColumns.includes(columnID)) {
          this.setState((state) => ({
            ...state,
            hiddenColumns: [
              ...state.hiddenColumns.filter((colID) => colID !== columnID),
            ],
          }));
        } else {
          this.setState((state) => ({
            ...state,
            hiddenColumns: [...state.hiddenColumns, columnID],
          }));
        }
      }
    );
  };

  private applyFormatters = (dataTable: GoogleDataTable, formatters: any[]) => {
    const { google } = this.props;
    for (let formatter of formatters) {
      switch (formatter.type) {
        case "ArrowFormat": {
          const vizFormatter = new google.visualization.ArrowFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "BarFormat": {
          const vizFormatter = new google.visualization.BarFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "ColorFormat": {
          const vizFormatter = new google.visualization.ColorFormat(
            formatter.options
          );
          const { ranges } = formatter;
          for (let range of ranges) {
            vizFormatter.addRange(...range);
          }
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "DateFormat": {
          const vizFormatter = new google.visualization.DateFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "NumberFormat": {
          const vizFormatter = new google.visualization.NumberFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "PatternFormat": {
          const vizFormatter = new google.visualization.PatternFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
      }
    }
  };
  private getColumnID = (dataTable: GoogleDataTable, columnIndex: number) => {
    return (
      dataTable.getColumnId(columnIndex) ||
      dataTable.getColumnLabel(columnIndex)
    );
  };
  private draw = async ({
    data,
    diffdata,
    rows,
    columns,
    options,
    legend_toggle,
    legendToggle,
    chartType,
    formatters,
    spreadSheetUrl,
    spreadSheetQueryParameters,
  }: ReactGoogleChartProps) => {
    const { google, googleChartWrapper } = this.props;
    let dataTable: GoogleDataTable;
    let chartDiff = null;
    if (diffdata) {
      const oldData = google.visualization.arrayToDataTable(diffdata.old);
      const newData = google.visualization.arrayToDataTable(diffdata.new);
      chartDiff = google.visualization[chartType].prototype.computeDiff(
        oldData,
        newData
      );
    }
    if (data !== null) {
      if (Array.isArray(data)) {
        dataTable = google.visualization.arrayToDataTable(data);
      } else {
        dataTable = new google.visualization.DataTable(data);
      }
    } else if (rows && columns) {
      dataTable = google.visualization.arrayToDataTable([columns, ...rows]);
    } else if (spreadSheetUrl) {
      dataTable = (await loadDataTableFromSpreadSheet(
        google,
        spreadSheetUrl,
        spreadSheetQueryParameters
      )) as GoogleDataTable;
    } else {
      dataTable = google.visualization.arrayToDataTable([]);
    }
    const columnCount = dataTable.getNumberOfColumns();
    for (let i = 0; i < columnCount; i += 1) {
      const columnID = this.getColumnID(dataTable, i);
      if (this.state.hiddenColumns.includes(columnID)) {
        const previousColumnLabel = dataTable.getColumnLabel(i);
        const previousColumnID = dataTable.getColumnId(i);
        const previousColumnType = dataTable.getColumnType(i);
        dataTable.removeColumn(i);
        dataTable.addColumn({
          label: previousColumnLabel,
          id: previousColumnID,
          type: previousColumnType,
        });
      }
    }
    const chart = googleChartWrapper.getChart();
    if (googleChartWrapper.getChartType() === "Timeline") {
      chart && chart.clearChart();
    }
    googleChartWrapper.setChartType(chartType);
    googleChartWrapper.setOptions(options || {});
    googleChartWrapper.setDataTable(dataTable);
    googleChartWrapper.draw();
    if (this.props.googleChartDashboard !== null) {
      this.props.googleChartDashboard.draw(dataTable);
    }

    if (chartDiff) {
      googleChartWrapper.setDataTable(chartDiff);
      googleChartWrapper.draw();
    }
    if (formatters) {
      this.applyFormatters(dataTable, formatters);
      googleChartWrapper.setDataTable(dataTable);
      googleChartWrapper.draw();
    }
    if (legendToggle === true || legend_toggle === true) {
      this.grayOutHiddenColumns({ options });
    }
    return;
  };
  private grayOutHiddenColumns = ({
    options,
  }: {
    options: ReactGoogleChartProps["options"];
  }) => {
    const { googleChartWrapper } = this.props;
    const dataTable = googleChartWrapper.getDataTable();
    if (!dataTable) return;
    const columnCount = dataTable.getNumberOfColumns();
    const hasAHiddenColumn = this.state.hiddenColumns.length > 0;
    if (hasAHiddenColumn === false) return;
    const colors = Array.from({ length: columnCount - 1 }).map(
      (dontcare, i) => {
        const columnID = this.getColumnID(dataTable, i + 1);
        if (this.state.hiddenColumns.includes(columnID)) {
          return GRAY_COLOR;
        } else if (options && options.colors) {
          return options.colors[i];
        } else {
          return DEFAULT_CHART_COLORS[i];
        }
      }
    );
    googleChartWrapper.setOptions({
      ...options,
      colors,
    });
    googleChartWrapper.draw();
  };
  private onResize = () => {
    const { googleChartWrapper } = this.props;
    googleChartWrapper.draw();
  };
  componentDidMount() {
    this.draw(this.props);
    window.addEventListener("resize", this.onResize);
    if (this.props.legend_toggle || this.props.legendToggle) {
      this.listenToLegendToggle();
    }
  }

  componentWillUnmount() {
    const { google, googleChartWrapper } = this.props;
    window.removeEventListener("resize", this.onResize);
    google.visualization.events.removeAllListeners(googleChartWrapper);
    if (googleChartWrapper.getChartType() === "Timeline") {
      googleChartWrapper.getChart() &&
        googleChartWrapper.getChart().clearChart();
    }
  }

  componentDidUpdate() {
    this.draw(this.props);
  }
  render() {
    return null;
  }
}

export class GoogleChartDataTable extends React.Component<GoogleChartDataTableProps> {
  componentDidMount() {}

  componentWillUnmount() {}
  shouldComponentUpdate() {
    return false;
  }
  render() {
    const { google, googleChartWrapper, googleChartDashboard } = this.props;
    return (
      <ContextConsumer
        render={(props) => {
          return (
            <GoogleChartDataTableInner
              {...props}
              google={google}
              googleChartWrapper={googleChartWrapper}
              googleChartDashboard={googleChartDashboard}
            />
          );
        }}
      />
    );
  }
}
import * as React from "react";
import {
  GoogleViz,
  GoogleChartWrapper,
  ReactGoogleChartProps,
  ReactGoogleChartEvent,
} from "../types";

import { ContextConsumer } from "../Context";

export type ChartDrawArgs = {
  data: ReactGoogleChartProps["data"];
};

export interface Props {
  googleChartWrapper: GoogleChartWrapper;
  google: GoogleViz;
}

export interface ListenToEventsArgs {
  googleChartWrapper: GoogleChartWrapper;
  google: GoogleViz;
  chartEvents: ReactGoogleChartEvent[] | null;
}

export class GoogleChartEvents extends React.Component<Props> {
  shouldComponentUpdate() {
    return false;
  }
  listenToEvents({
    chartEvents,
    google,
    googleChartWrapper,
  }: ListenToEventsArgs) {
    if (!chartEvents) {
      return;
    }
    google.visualization.events.removeAllListeners(googleChartWrapper);
    for (let event of chartEvents) {
      const { eventName, callback } = event;
      google.visualization.events.addListener(
        googleChartWrapper,
        eventName,
        (...args: any[]) => {
          callback({
            chartWrapper: googleChartWrapper,
            props: this.props as any,
            google: google,
            eventArgs: args,
          });
        }
      );
    }
  }
  render() {
    const { google, googleChartWrapper } = this.props;
    return (
      <ContextConsumer
        render={(propsFromContext) => {
          this.listenToEvents({
            chartEvents: propsFromContext.chartEvents || null,
            google,
            googleChartWrapper,
          });
          return null;
        }}
      />
    );
  }
}
import * as React from "react";
import {
  GoogleViz,
  GoogleChartWrapper,
  ReactGoogleChartProps,
  GoogleChartControlProp,
  GoogleChartControl,
  GoogleChartDashboard,
  GoogleChartEditor,
} from "../types";
import { generateUniqueID } from "../generate-unique-id";
import { GoogleChartDataTable } from "./GoogleChartDataTable";
import { GoogleChartEvents } from "./GoogleChartEvents";

export type Props = {
  google: GoogleViz;
  graphID?: string | null;
  graph_id?: string | null;
  options?: ReactGoogleChartProps["options"];
  chartWrapperParams?: {};
  chartType: ReactGoogleChartProps["chartType"];
  width?: ReactGoogleChartProps["width"];
  height?: ReactGoogleChartProps["height"];
  style?: ReactGoogleChartProps["style"];
  className?: ReactGoogleChartProps["className"];
  rootProps?: ReactGoogleChartProps["rootProps"];
} & ReactGoogleChartProps;

export interface State {
  googleChartWrapper: GoogleChartWrapper | null;
  isReady: boolean;
  googleChartDashboard: GoogleChartDashboard | null;
  googleChartEditor: GoogleChartEditor | null;
  googleChartControls:
    | { control: GoogleChartControl; controlProp: GoogleChartControlProp }[]
    | null;
}

let controlCounter = 0;

export class GoogleChart extends React.Component<Props, State> {
  state = {
    googleChartWrapper: null,
    googleChartDashboard: null,
    googleChartControls: null,
    googleChartEditor: null,
    isReady: false,
  } as State;
  graphID: null | string = null;
  private dashboard_ref: React.RefObject<HTMLDivElement> = React.createRef();
  private toolbar_ref: React.RefObject<HTMLDivElement> = React.createRef();
  private getGraphID = () => {
    const { graphID, graph_id } = this.props;
    let instanceGraphID: string;
    if (!graphID && !graph_id) {
      if (!this.graphID) {
        instanceGraphID = generateUniqueID();
      } else {
        instanceGraphID = this.graphID;
      }
    } else if (graphID && !graph_id) {
      instanceGraphID = graphID as string;
    } else if (graph_id && !graphID) {
      instanceGraphID = graph_id as string;
    } else {
      instanceGraphID = graphID as string;
    }
    this.graphID = instanceGraphID;
    return this.graphID as string;
  };
  private getControlID = (id: undefined | string, index: number) => {
    controlCounter += 1;
    let controlID: string;
    if (typeof id === "undefined") {
      controlID = `googlechart-control-${index}-${controlCounter}`;
    } else {
      controlID = id;
    }
    return controlID;
  };

  addControls = (
    googleChartWrapper: GoogleChartWrapper,
    googleChartDashboard: GoogleChartDashboard
  ) => {
    const { google, controls } = this.props;

    const googleChartControls = !controls
      ? null
      : controls.map((control, i) => {
          const {
            controlID: controlIDMaybe,
            controlType,
            options: controlOptions,
            controlWrapperParams,
          } = control;
          const controlID = this.getControlID(controlIDMaybe, i);
          return {
            controlProp: control,
            control: new google.visualization.ControlWrapper({
              containerId: controlID,
              controlType,
              options: controlOptions,
              ...controlWrapperParams,
            }),
          };
        });
