      const { control, controlProp } = chartControl;
      const { controlEvents = [] } = controlProp;
      for (let event of controlEvents) {
        const { callback, eventName } = event;
        google.visualization.events.removeListener(
          control,
          eventName,
          callback
        );
        google.visualization.events.addListener(
          control,
          eventName,
          (...args: any[]) => {
            callback({
              chartWrapper: googleChartWrapper,
              controlWrapper: control,
              props: this.props as any,
              google: google,
              eventArgs: args,
            });
          }
        );
      }
    }
    return googleChartControls;
  };

  componentDidMount() {
    const {
      options,
      google,
      chartType,
      chartWrapperParams,
      toolbarItems,
      getChartEditor,
      getChartWrapper,
    } = this.props;

    const chartConfig = {
      chartType,
      options,
      containerId: this.getGraphID(),
      ...chartWrapperParams,
    };
    const googleChartWrapper = new google.visualization.ChartWrapper(
      chartConfig
    );
    googleChartWrapper.setOptions(options || {});
    if (getChartWrapper) {
      getChartWrapper(googleChartWrapper, google);
    }
    const googleChartDashboard = new google.visualization.Dashboard(
      this.dashboard_ref
    );

    const googleChartControls = this.addControls(
      googleChartWrapper,
      googleChartDashboard
    );
    if (toolbarItems) {
      google.visualization.drawToolbar(
        this.toolbar_ref.current as HTMLDivElement,
        toolbarItems
      );
    }
    let googleChartEditor: null | GoogleChartEditor = null;
    if (getChartEditor) {
      googleChartEditor = new google.visualization.ChartEditor();
      getChartEditor({
        chartEditor: googleChartEditor,
        chartWrapper: googleChartWrapper,
        google,
      });
    }

    this.setState({
      googleChartEditor,
      googleChartControls: googleChartControls,
      googleChartDashboard: googleChartDashboard,
      googleChartWrapper,
      isReady: true,
    });
  }
  componentDidUpdate() {
    if (!this.state.googleChartWrapper) return;
    if (!this.state.googleChartDashboard) return;
    if (!this.state.googleChartControls) return;

    const { controls } = this.props;
    if (controls) {
      for (let i = 0; i < controls.length; i += 1) {
        const { controlType, options, controlWrapperParams } = controls[i];
        if (controlWrapperParams && "state" in controlWrapperParams) {
          this.state.googleChartControls[i].control.setState(
            controlWrapperParams["state"]
          );
        }
        this.state.googleChartControls[i].control.setOptions(options);
        this.state.googleChartControls[i].control.setControlType(controlType);
      }
    }
  }
  shouldComponentUpdate(nextProps: Props, nextState: State) {
    return (
      this.state.isReady !== nextState.isReady ||
      nextProps.controls !== this.props.controls
    );
  }
  renderChart = () => {
    const { width, height, options, style, className, rootProps, google } =
      this.props;

    const divStyle = {
      height: height || (options && options.height),
      width: width || (options && options.width),
      ...style,
    };
    return (
      <div
        id={this.getGraphID()}
        style={divStyle}
        className={className}
        {...rootProps}
      >
        {this.state.isReady && this.state.googleChartWrapper !== null ? (
          <React.Fragment>
            <GoogleChartDataTable
              googleChartWrapper={this.state.googleChartWrapper}
              google={google}
              googleChartDashboard={this.state.googleChartDashboard}
            />
            <GoogleChartEvents
              googleChartWrapper={this.state.googleChartWrapper}
              google={google}
            />
          </React.Fragment>
        ) : null}
      </div>
    );
  };
  renderControl = (
    filter = ({
      control,
      controlProp,
    }: {
      control: GoogleChartControl;
      controlProp: GoogleChartControlProp;
    }) => true
  ) => {
    return this.state.isReady && this.state.googleChartControls !== null ? (
      <React.Fragment>
        {this.state.googleChartControls
          .filter(({ controlProp, control }) => {
            return filter({ control, controlProp });
          })
          .map(({ control, controlProp }) => {
            return (
              <div
                key={control.getContainerId()}
                id={control.getContainerId()}
              />
            );
          })}
      </React.Fragment>
    ) : null;
  };
  renderToolBar = () => {
    if (!this.props.toolbarItems) return null;
    return <div ref={this.toolbar_ref} />;
  };
  render() {
    const { width, height, options, style } = this.props;

    const divStyle = {
      height: height || (options && options.height),
      width: width || (options && options.width),
      ...style,
    };
    if (this.props.render) {
      return (
        <div ref={this.dashboard_ref} style={divStyle}>
          <div ref={this.toolbar_ref} id="toolbar" />
          {this.props.render({
            renderChart: this.renderChart,
            renderControl: this.renderControl,
            renderToolbar: this.renderToolBar,
          })}
        </div>
      );
    } else {
      return (
        <div ref={this.dashboard_ref} style={divStyle}>
          {this.renderControl(({ controlProp }) => {
            return controlProp.controlPosition !== "bottom";
          })}
          {this.renderChart()}
          {this.renderControl(({ controlProp }) => {
            return controlProp.controlPosition === "bottom";
          })}
          {this.renderToolBar()}
        </div>
      );
    }
  }
}
import * as React from "react";

import {
  GoogleViz,
  ReactGoogleChartProps,
  ReactGoogleChartState,
} from "./types";
import { LoadGoogleCharts } from "./hooks";
import { chartDefaultProps } from "./default-props";
import { GoogleChart } from "./components/GoogleChart";
import { ContextProvider } from "./Context";

export class Chart extends React.Component<
  ReactGoogleChartProps,
  ReactGoogleChartState
> {
  _isMounted = false;

  state = {
    loadingStatus: "loading" as ReactGoogleChartState["loadingStatus"],
    google: null as ReactGoogleChartState["google"],
  };

  static defaultProps = chartDefaultProps;

  render() {
    const {
      chartLanguage,
      chartPackages,
      chartVersion,
      mapsApiKey,
      loader,
      errorElement,
    } = this.props;
    return (
      <ContextProvider value={this.props as ReactGoogleChartProps}>
        {this.state.loadingStatus === "ready" && this.state.google !== null ? (
          <GoogleChart
            {...(this.props as ReactGoogleChartProps)}
            google={this.state.google}
          />
        ) : this.state.loadingStatus === "errored" && errorElement ? (
          errorElement
        ) : (
          loader
        )}
        <LoadGoogleCharts
          chartLanguage={chartLanguage}
          chartPackages={chartPackages}
          chartVersion={chartVersion}
          mapsApiKey={mapsApiKey}
          onLoad={this.onLoad}
          onError={this.onError}
        />
      </ContextProvider>
    );
  }

  componentDidMount() {
    this._isMounted = true;
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  onLoad = (google: GoogleViz) => {
    if (this.props.onLoad) {
      this.props.onLoad(google);
    }
    if (this.isFullyLoaded(google)) {
      this.onSuccess(google);
    } else {
      // IE11: window.google is not fully set, we have to wait
      const id = setInterval(() => {
        const google = (
          window as Window & {
            google?: GoogleViz;
          }
        ).google;

        if (this._isMounted) {
          if (google && this.isFullyLoaded(google)) {
            clearInterval(id);
            this.onSuccess(google);
          }
        } else {
          clearInterval(id);
        }
      }, 1000);
    }
  };

  onSuccess = (google: GoogleViz) => {
    this.setState({
      loadingStatus: "ready",
      google,
    });
  };

  onError = () => {
    this.setState({
      loadingStatus: "errored",
    });
  };

  isFullyLoaded(google: GoogleViz) {
    const { controls, toolbarItems, getChartEditor } = this.props;

    return (
      google &&
      google.visualization &&
      google.visualization.ChartWrapper &&
      google.visualization.Dashboard &&
      (!controls || google.visualization.ChartWrapper) &&
      (!getChartEditor || google.visualization.ChartEditor) &&
      (!toolbarItems || google.visualization.drawToolbar)
    );
  }
}

